if (typeof axis === 'undefined' || !axis) {
  var axis = {};
}

axis.Story = Backbone.Model.extend({
  initialize: function() {
    if (this.get('tags')) {
      var tags = this.get('tags');
      for(var i=0; i<tags.length; i++) {
        if(tags[i] === 'external')
          this.set('external', true);
      }
    }
  }
});

axis.StoryCollection = Backbone.Collection.extend({
  model: axis.Story
});

axis.NewsContainer = Backbone.View.extend({
  events : {
    'click .close': 'refresh',
    'click #tabs a': 'updateTabs'
  },

  initialize: function() {
    this.checkTabs();

    $(window).on('resize', this.checkTabs);
  },

  checkTabs: function() {
    if(window.innerWidth > 640) {
      $(document).find('.tab-pane').addClass('active').show();
    } else {
      $activeTabId = $('#tabs-bar #tabs li.active a').attr('href');
      _.each($('.tab-content').children(), function(element) {
        if('#' + $(element).attr('id') !== $activeTabId) {
          $(element).hide();
        }
      });
    }
  },

  updateTabs: function(event) {
    event.preventDefault();

    $('.tab-pane.active').removeClass('active').hide();
    $(event.target.hash).addClass('active').show();
  },

  refresh: function() {
    this.$el.find('.single-story').remove();

    // The twitter widget hangs around in the DOM. Need to remove it explicity
    $('#twitter-wjs').remove();
    
    this.$el.children().show();
    
    if(window.innerWidth > 640) {
      this.$el.find('#tabs-bar').hide();
    } else {
      $('.project-header').hide();
    }

    axis.router.navigate('/project/avi/');
  }
});

axis.Features = Backbone.View.extend({
  initialize: function() {
    axis.featureStories = new axis.StoryCollection(axis.fakeFeatureData);

    var features = axis.featureStories.map(function(story){
      return (new axis.FeatureItem({model: story}).render().el);
    });

    this.$el.find('#featured').html(features);

    $(window).load(function() {
      $('#featured').orbit({
        timer: 'false'
      });
    });
  }
});

axis.FeatureItem = Backbone.View.extend({
  tagName: 'div',

  initialize: function() {
    this.template = _.template($('#feature-story-carousel-template').html());
  },

  render: function(top) {
    this.$el.html(this.template(this.model.toJSON())).attr('data-caption', '#feature-caption-' + this.model.id);
    return this;
  }
});

axis.NewsFeed = Backbone.View.extend({
  initialize: function() {
    axis.feedStories = new axis.StoryCollection(axis.fakeNewsFeedData);

    var feed = axis.feedStories.map(function(story){
      return (new axis.NewsFeedItem({model: story}).render().el);
    });

    this.$el.find('.items').append(feed);
  }
});

axis.NewsFeedItem = Backbone.View.extend({
  tagName: 'article',
  className: 'story',

  events: {
    'click a' : 'open'
  },

  initialize: function() {
    this.template = _.template($('#news-feed-story-template').html());
    this.externalTemplate = _.template($('#external-news-feed-story-template').html());
  },

  render: function() {
    if (this.model.get('external') === true) {
      this.$el.html(this.externalTemplate(this.model.toJSON()));
    } else {
      this.$el.html(this.template(this.model.toJSON()));
    }
    return this;
  },

  open: function(event) {
    if (event.target.className !== 'external') {
      event.preventDefault();
    
      $newsview = $(document).find('#news-container');
      $newsview.children().hide();
      $newsview.prepend(
        new axis.SingleStory({model: this.model}).render().el
      );

      axis.router.navigate('/article/' + this.model.get('slug'));
    
      // Scroll to headline
      $('body').scrollTop($("header h2").offset().top - 50);

      // Init Affix
      $('.moving-container').affix({
        offset: {
          top: 200,
          bottom: 350
        }
      });
    }
  }
});

axis.ToolsAndData = Backbone.View.extend({
  initialize: function() {
    axis.tools = new axis.StoryCollection(axis.toolsData);

    var tools = axis.tools.map(function(tool){
      return (new axis.ToolsAndDataItem({model: tool}).render().el);
    });

    this.$el.find('.items').append(tools);
  }
});

axis.ToolsAndDataItem = Backbone.View.extend({
  tagName: 'div',
  className: 'tool',

  initialize: function() {
    this.template = _.template($('#tool-and-data-template').html());
  },

  render: function() {
    this.$el.html(this.template(this.model.toJSON()));
    return this;
  }
});

axis.SingleStory = Backbone.View.extend({
  tagName: 'div',
  className: 'single-story view row-fluid',

  events: {
    'click .story-navigation a': 'open'
  },

  initialize: function() {
    this.template = _.template($('#single-story-template').html());
    this.prevStory = axis.feedStories.get(parseInt(this.model.id, 0) - 1);
    this.nextStory = axis.feedStories.get(parseInt(this.model.id, 0) + 1);
  },

  open: function() {
    // this should open a story from story nav
    // we should also be able to open stories from the sidebar
    // but maybe we should create a view for the side bar
  },

  render: function() {
    // Add story navigation
    window.storyNav = new axis.StoryNavigation({
      prev: this.prevStory,
      next: this.nextStory
    }).render().el;
    
    this.$el.html(this.template(this.model.toJSON()));
    this.$el.find('.story-navigation').append(storyNav);
    return this;
  }
});

axis.StorySideBar = Backbone.View.extend({
  //sidebar for single story
  //populate recent links from storycollection
  //speaking of story collections, there should be on collection
  //then we can filter for features
});

axis.StoryNavigation = Backbone.View.extend({
  tagName: 'ul',

  initialize: function() {
    this.previous = new axis.StoryNavigationItem({
      model: this.options.prev,
      className: 'previous span6',
      direction: 'Previous'
    }).render().el;

    this.next = new axis.StoryNavigationItem({
      model: this.options.next,
      className: 'next span6',
      direction: 'Next'
    }).render().el;
  },

  render: function() {
    this.$el.append(this.previous, this.next);
    return this;
  }
});

axis.StoryNavigationItem = Backbone.View.extend({
  tagName: 'li',

  initialize: function() {
    this.template = _.template($('#story-navigation-item-template').html());
  },

  render: function(){
    this.$el.html(this.template(this.model.toJSON()));
    return this;
  }
});

axis.Router = Backbone.Router.extend({
  routes: {
    "article/:slug/": "article",
    "project/:name/": "project",
    "": "home"
  },

  initialize: function() {
    // Faster clicking on mobile
    window.addEventListener('load', function() {
      new FastClick(document.body);
    }, false);

    this.createSubViews();

    Backbone.history.start({pushState:true, silent:true}); //, root: '/~cpt/axis/'});
  },

  createSubViews: function() {
    axis.NewsContainer = new axis.NewsContainer({el: '#news-container'});
    axis.NewsFeed = new axis.NewsFeed({el: '#news-feed'});
    axis.Features = new axis.Features({el: '#feature-container'});
    axis.ToolsAndData = new axis.ToolsAndData({el: '#tools-and-data'});
  },

  home: function() {
    //do nothing
  },

  article: function() {
    //axis.newsContainer.openStory(event);
  },

  project: function(name) {
    axis.NewsContainer.refresh();
  }
});

